#!/bin/bash
npm start &
sleep 2

for j in 17 18 19 20 21 22
do
 for i in $(eval echo {$1..$2})
    do



sudo sysdig -w attack$j/stream-$i-orig.scap -z -s 4096 proc.name!=criu and proc.name!=init and proc.name!=systemd-udevd and proc.name!=upstart-udev-br and proc.name!=upstart-socket- and proc.name!=upstart-file-br and proc.name!=sh and proc.name!=iptables and proc.name!=tcpdump &
  
sudo nohup tcpdump -i wlp1s0 -s0 -w attack$j/stream-$i.cap >pdump.out &

sleep 2

curl -v --cookie "" http://127.0.0.1:9000/ &

tail -f fifo1 | nc -l 127.0.0.1 1337 & 


cat test$j > fifo1



sleep 5
sudo killall -s SIGINT sysdig
sudo killall -s SIGINT tcpdump
#sudo killall -s SIGINT node
done

for i in $(eval echo {$1..$2}) 
    do
       sudo sysdig -r attack$j/stream-$i-orig.scap > attack$j/stream-$i.scap
       sudo rm attack$j/stream-$i-orig.scap

    done

done
